mapper.select:
select p.product_id, p.seller_id, u.nickname, u.job, p.title, pi.b_price, p.filename1, avg(r.star_point) as avg_star_point, count(r.review_id) as review_count, p.createdAt from product p left join user u on u.user_id = p.seller_id left join review r on p.product_id = r.product_id left join price_info pi on p.product_id = pi.product_price_id WHERE (REPLACE(p.title, ' ', '') like '%${keyword}%' or p.title like '%${keyword}%' or REPLACE(p.content, ' ', '') like '%${keyword}%' or p.content like '%${keyword}%')
and category= '${category}'
group by p.product_id order by ${sort} desc limit #{paginationInfo.firstRecordIndex}, #{recordsPerPage};


select count(*)
from product
where category = 'design';


select count(*)
from product
where category = 'video';


select count(*)
from product
where category = 'it';
