mapper.select:

select count(*)
from orders
where buyer_id=#{user_id}


select count(*)
from orders
where seller_id=#{user_id}


select sum(order_price)
from orders
where buyer_id=#{user_id}


select sum(order_price)
from orders
where seller_id=#{user_id}


select count(*)
from likes
where like_userid=#{user_id}


select count(*)
from likes
where liked_userid=#{user_id}


select count(*)
from user
where nickname=#{nick}


select count(*)
from user
where user_id=#{user_id} and password=#{password}
